import classes from './CartButton.module.css';
import { useDispatch ,useSelector } from 'react-redux';
import { uiActions } from '../../store/ui-slice';
import {useState} from "react";


const CartButton = (props) => {
    const [mouse, setMouse] = useState(false);
  const dispatch = useDispatch();
  const cartQuantity = useSelector(state => state.cart.totalQuantity);

 const toggleCartHandler = () => {
   dispatch(uiActions.toggle());
 }

  return (
    <div className={`${classes.wrapper}`}  onClick={toggleCartHandler}>
       <div
           onMouseEnter={() => {
               setMouse(true);
           }}
           onMouseLeave={() => {
               setMouse(false);
           }}
       >
           <img className={classes['accordion-toggle']} src="data:image/png;base64,"/>
       </div>
        {mouse ?
            <div className={classes['basket-top-menu']}>
              <div className={classes['pagecontainer']}>
                <div className={classes['sweet-basket-container']}>
                  <div className={classes['sweet-basket-page-title']}>
                      <div className={classes['title-box']}>
                          <div className={classes['title-dot']}></div>
                          <div className={classes['title']}>سبد خرید</div>
                      </div>
                  </div>
                    <div>
                        <div className={classes['d-flex justify-content-center flex-column']}>
                           <div className={classes['sweet-basket-product-container']}>
                               <div className={classes['sweet-product-title-basket']}></div>
                               <p></p>
                           </div>
                            <div></div>
                        </div>
                    </div>
                </div>
              </div>
            </div>
       :null }
      </div>

  );
};
export default CartButton;
