import Card1 from '../UI/Card1';
import classes from './Cart.module.css';
import CartItem from './CartItem';
import { useSelector } from 'react-redux';



const Cart = (props) => {

    const cartItems =useSelector(state => state.cart.items)

    return (
    <Card1 className={classes.cart}>
      <div className={classes.title}>
        <div className={classes.dot}></div>
        <div className={classes.text}>سبد خرید</div>
      </div>
      <ul>
       {cartItems.map((item)=>(
         <CartItem
         key={item.id}
         item={{
           id: item.id,
           title: item.title,
           quantity:item.quantity ,
           total:item.totalPrice,
           price: item.price
         }}
       />
       ))}
       <br/>
        <div className="d-grid gap-2  mx-auto ">
          <button className="btn btn-light bordar border-danger rounded-pill text-danger" type="button">ویرایش سبد خرید</button>
          <button className="btn btn-danger rounded-pill" type="button">
          <div >
            <div className="row">

               <div className="col-sm d-flex">col-sm</div>
               <div className="col-sm d-flex justify-content-end">: جمع کل</div>
           </div>
         </div>
          </button>
          <button className="btn btn-danger rounded-pill" type="button">نهایی کردن سفارش</button>
      </div>
      </ul>
    </Card1>
  );
};

export default Cart;
