    //   {/* <div className={classes.details}>
    //     <div className={classes.actions}>
    //       <button onClick={removeItemHandler}>-</button>
    //       <button onClick={addItemHandler}>+</button>
    //     </div>
    //   </div> */}
    // </li>

          <div className="page-part-card">
              <div className="pagecontainer">
                  <div className="sweet-basket-container">
                      <div className="sweet-basket-page-title">
                          <div className="title-box">
                              <div className="title-dot"></div>
                              <div className="title">سبد خرید</div>
                          </div>
                      </div>
                      <div>
                          <div className="d-flex justify-content-center flex-column">
                              <div className="sweet-readonly-basket-product-container">
                                  <div className="sweet-product-title-basket">
                                      <div className="title">لقمه کباب<span className="subtitle">(ویژه)</span></div>
                                      <div className="line-container">
                                          <div className="line"></div>
                                      </div>
                                  </div>
                                  <div className="sweet-product-title-basket">
                                      <div className="title">تعداد</div>
                                      <div className="line-container">
                                          <div className="line"></div>
                                      </div>
                                      <div className="value">1 پرس</div>
                                  </div>
                                  <div className="sweet-product-title-basket">
                                      <div className="title">قیمت</div>
                                      <div className="line-container">
                                          <div className="line"></div>
                                      </div>
                                      <div className="value">80,000 تومان</div>
                                  </div>
                                  <div className="bottom-separator">
                                      <div></div>
                                  </div>
                              </div>
                          </div>
                          <div className="sweet-total-price-basket-container">
                              <div className="sweet-edit-basket-button">
                                  <div><span>ویرایش سبد خرید </span></div>
                              </div>
                              <div className="sweet-register-order-basket">
                                  <div><span>جمع کل :</span><span>80,000 تومان</span></div>
                              </div>
                              <a href="/location/place/select/basket.preorder.placeid">
                                  <div className="sweet-btn sweet-register-order-basket-style">
                                      <span>نهایی کردن سفارش</span></div>
                              </a></div>
                      </div>
                  </div>
              </div>
          </div>

  );
};

export default CartItem;

