import classes from './CartButton.module.css';
import { useDispatch ,useSelector } from 'react-redux';
import { uiActions } from '../../store/ui-slice';


const CartButton = (props) => {
  const dispatch = useDispatch();
  const cartQuantity = useSelector(state => state.cart.totalQuantity);

 const toggleCartHandler = () => {
   dispatch(uiActions.toggle());
 }

  return (
    <div className={`${classes.wrapper}`}  onClick={toggleCartHandler}>

        <img className={classes['accordion-toggle']} src="data:image/png;base64,"/>
        {/* {cartQuantity > 0 ? (
                <div className={classes.badge}>
                     {cartQuantity}
                 </div>
            ) : null} */}
        <div className={classes['accordion-content']}>
            <div className={classes['accordion-inner']}>
                <p>For animate the "height" of element with CSS Transitions you need use "max-height".</p>
                <p>If use the "height: auto", the effect not works. Is necessary some value for the CSS create a CSS
                    animate, and you can use "max-height" with a great value for emulate this effect.</p>
            </div>
        </div>
      </div>

  );
};
export default CartButton;
