import classes from './CartItem.module.css';
import { useDispatch } from 'react-redux';
import { cartActions } from '../../store/cart-slice';


const CartItem = (props) => {
  const dispatch = useDispatch();
  const { title, quantity, total, price ,id} = props.item;

  const removeItemHandler = () => {
    dispatch(cartActions.removeItemFromCart(id));
  };

  const addItemHandler = () => {
    dispatch(cartActions.addItemToCart({
      id,
      title,
      price,
    }));
  };


  return (
    <li >
      <header>
        {/* <h3>{title}</h3> */}
        <div className={classes.product-title}>
           <div className={classes.title}>
             <span className={classes.subtitle}>(ویژه)</span>
             {title}
           </div>
          <div className={classes.line1}>
            <div className={classes.line}></div>
          </div>
        </div>

        <div className={classes.product}>
          <div className={classes.title}>تعداد</div>
            <div className={classes.value}>{quantity}پرس</div>
            <div className={classes.line1}>

            </div>

        </div>
        <div className={classes.product}>
           <div className={classes.value}>{total}</div>
            <div className={classes.line1}>

            </div>
          <div className={classes.title}>قیمت</div>

        </div>
        <hr className={classes.dashed}></hr>
      </header>
      {/* <div className={classes.details}>
        <div className={classes.actions}>
          <button onClick={removeItemHandler}>-</button>
          <button onClick={addItemHandler}>+</button>
        </div>
      </div> */}
    </li>
  );
};

export default CartItem;

